import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Box, Button, FormControl, FormLabel, Input, Stack, Heading, Flex } from '@chakra-ui/react';

const Login = () => {
  // State to hold email and password input values
  const navigate = useNavigate(); // React Router hook for navigation
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  // Function to handle form submission
  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault(); // Prevent the form from refreshing the page
    console.log("Form submitted!"); // Debug log to check if this function is called
    console.log("Email:", email); // Log the email
    console.log("Password:", password); // Log the password
    alert(email+","+password)
  };

  return (
    <Flex
      minHeight="100vh"
      align="center"
      justify="center"
      bg="gray.50"
    >
      <Box
        p={8}
        maxWidth="400px"
        borderWidth={1}
        borderRadius={8}
        boxShadow="lg"
        bg="white"
      >
        <Box textAlign="center" mb={6}>
          <Heading as="h2" size="lg">Sign In</Heading>
        </Box>
        {/* Form submission handled here */}
        <form onSubmit={handleSubmit}>
          <Stack spacing={4}>
            {/* Email input */}
            <FormControl id="email" isRequired>
              <FormLabel>Email address</FormLabel>
              <Input
                type="email"
                placeholder="Enter your email"
                value={email}
                onChange={(e) => setEmail(e.target.value)} // Update email state
              />
            </FormControl>
            {/* Password input */}
            <FormControl id="password" isRequired>
              <FormLabel>Password</FormLabel>
              <Input
                type="password"
                placeholder="Enter your password"
                value={password}
                onChange={(e) => setPassword(e.target.value)} // Update password state
              />
            </FormControl>
            {/* Sign in button */}
            <Button colorScheme="teal" size="lg" type="submit" width="full">
              Sign In
            </Button>
          </Stack>
        </form>
      </Box>
    </Flex>
  );
};

export default Login;
