import React from 'react';
import { useNavigate } from 'react-router-dom';
import { Box, Button, FormControl, FormLabel, Input, Stack, Heading, Flex } from '@chakra-ui/react';

const Login = () => {
  const navigate = useNavigate();

  const handleLogin = (e: React.FormEvent) => {
    e.preventDefault();
    // Simulate successful login and navigate to the dashboard
    navigate('/dashboard');
  };

  const goToDashboard = () => {
    navigate('/dashboard'); // Temporarily go to dashboard
  };

  return (
    <Flex
      minHeight="100vh"
      align="center"
      justify="center"
      bg="gray.50"
    >
      <Box
        p={8}
        maxWidth="400px"
        borderWidth={1}
        borderRadius={8}
        boxShadow="lg"
        bg="white"
      >
        <Box textAlign="center" mb={6}>
          <Heading as="h2" size="lg">Sign In</Heading>
        </Box>
        <form onSubmit={handleLogin}>
          <Stack spacing={4}>
            <FormControl id="email" isRequired>
              <FormLabel>Email address</FormLabel>
              <Input type="email" placeholder="Enter your email" />
            </FormControl>
            <FormControl id="password" isRequired>
              <FormLabel>Password</FormLabel>
              <Input type="password" placeholder="Enter your password" />
            </FormControl>
            <Button colorScheme="teal" size="lg" type="submit" width="full">
              Sign In
            </Button>
          </Stack>
        </form>

        {/* Temporary button to navigate to the dashboard */}
        <Button
          mt={4}
          colorScheme="blue"
          size="lg"
          width="full"
          onClick={goToDashboard}
        >
          Go to Dashboard
        </Button>
      </Box>
    </Flex>
  );
};

export default Login;
