import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';  // Import useNavigate
import { Box, Button, FormControl, FormLabel, Input, Stack, Heading, Flex, Text } from '@chakra-ui/react';
import axios from 'axios';

const Login = () => {
  const navigate = useNavigate();  // Initialize navigate for navigation
  const [email, setEmail] = useState('');  // State for email input
  const [password, setPassword] = useState('');  // State for password input
  const [errorMessage, setErrorMessage] = useState('');  // State for showing error messages

  useEffect(() => {
    console.log("Login component mounted");
  }, []);  // This should print when the component mounts

  // Function that gets triggered when login form is submitted
  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();  // Prevent default form submit behavior

    try {
      const response = await axios.post('http://localhost:5000/api/Students/Login', { email, password });

      if (response.status === 200) {
        const { userType } = response.data;  // Extract userType from the response data

        // Redirect based on userType (1 for Admin, 2 for Student)
        if (userType === 1) {
          navigate('/admin-dashboard');  // Navigate to admin dashboard if userType is 1 (Admin)
        } else if (userType === 2) {
          navigate('/student-dashboard');  // Navigate to student dashboard if userType is 2 (Student)
        }
      }
    } catch (error) {
      setErrorMessage('Login failed. Please check your credentials and try again.');
    }
  };

  return (
    <Flex minHeight="100vh" align="center" justify="center" bg="gray.50">
      <Box p={8} maxWidth="400px" borderWidth={1} borderRadius={8} boxShadow="lg" bg="white">
        <Box textAlign="center" mb={6}>
          <Heading as="h2" size="lg">Sign In</Heading>
        </Box>
        <form onSubmit={handleLogin}>
          <Stack spacing={4}>
            <FormControl id="email" isRequired>
              <FormLabel>Email address</FormLabel>
              <Input
                type="email"
                placeholder="Enter your email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </FormControl>
            <FormControl id="password" isRequired>
              <FormLabel>Password</FormLabel>
              <Input
                type="password"
                placeholder="Enter your password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </FormControl>
            <Button colorScheme="teal" size="lg" type="submit" width="full">
              Sign In
            </Button>
          </Stack>
        </form>
        {errorMessage && <Text color="red.500" mt={4}>{errorMessage}</Text>}
      </Box>
    </Flex>
  );
};

export default Login;
