import React, { useState, useEffect } from 'react';
import { Box, Heading, Text, Button, Stack, Flex, Grid, Table, Thead, Tbody, Tr, Th, Td, useDisclosure } from '@chakra-ui/react';
import { Modal, ModalOverlay, ModalContent, ModalHeader, ModalFooter, ModalBody, ModalCloseButton } from '@chakra-ui/react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';

interface Enrollment {
  courseName: string;
  completionStatus: string;
  progress?: number;
}

interface Course {
  courseId: number;
  courseName: string;
  description: string;
  maxSeats: number;
  currentSeats: number;
  startDate: string;
  endDate: string;
}

const Dashboard: React.FC = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { fullName, userId } = location.state || {};

  // State to hold the student's enrollments
  const [enrollments, setEnrollments] = useState<Enrollment[]>([]);
  // State to hold the list of available courses
  const [availableCourses, setAvailableCourses] = useState<Course[]>([]);
  // Modal control state
  const { isOpen, onOpen, onClose } = useDisclosure();
  const [modalMessage, setModalMessage] = useState<string | null>(null);

  useEffect(() => {
    const fetchEnrollments = async () => {
      try {
        const response = await axios.get('http://localhost:5000/GetAllEnrollments');
        const studentEnrollments = response.data.data.filter((enrollment: any) => enrollment.studentName === fullName);

        const processedEnrollments = studentEnrollments.map((enrollment: any) => {
          let progress = 0;
          if (enrollment.completionStatus === "1") {
            progress = 100;
          } else {
            progress = Math.floor(Math.random() * (90 - 20 + 1)) + 20;
          }
          return {
            courseName: enrollment.courseName,
            completionStatus: enrollment.completionStatus,
            progress,
          };
        });

        setEnrollments(processedEnrollments.slice(0, 3)); // Max 3 subjects
      } catch (error) {
        console.error('Error fetching enrollments:', error);
      }
    };

    fetchEnrollments();
  }, [fullName]);

  useEffect(() => {
    const fetchCourses = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/Course/ShowAllCourses');
        setAvailableCourses(response.data);
      } catch (error) {
        console.error('Error fetching available courses:', error);
      }
    };

    fetchCourses();
  }, []);

  const handleEnroll = async (courseName: string) => {
    try {
      const requestBody = {
        studentName: fullName,
        courseName: courseName,
      };

      // Log the request body for debugging purposes
      console.log('Request Body:', requestBody);

      // Make the API call to enroll the student in the course
      const response = await axios.post('http://localhost:5000/JoinCourse', requestBody);

      // Handle successful enrollment by showing the response message from the server
      if (response.data && response.data.Message) {
        setModalMessage(response.data.Message);  // Use the server's response message directly
      } else {
        setModalMessage('Successfully enrolled in the course.');  // Fallback message if no specific message from the server
      }

      onOpen();  // Open the modal to show the message

      // Refetch the enrollments after the student has enrolled in the course
      const fetchEnrollments = async () => {
        try {
          const response = await axios.get('http://localhost:5000/GetAllEnrollments');
          const studentEnrollments = response.data.data.filter((enrollment: any) => enrollment.studentName === fullName);

          const processedEnrollments = studentEnrollments.map((enrollment: any) => {
            let progress = 0;
            if (enrollment.completionStatus === "1") {
              progress = 100;
            } else {
              progress = Math.floor(Math.random() * (90 - 20 + 1)) + 20;
            }
            return {
              courseName: enrollment.courseName,
              completionStatus: enrollment.completionStatus,
              progress,
            };
          });

          setEnrollments(processedEnrollments.slice(0, 3));  // Update the enrollments to show the latest list
        } catch (error) {
          console.error('Error refetching enrollments:', error);
          setModalMessage('Error refetching enrollments. Please try again.');
          onOpen();
        }
      };

      fetchEnrollments();  // Fetch the updated list of enrollments

    } catch (error: any) {
      // Handle error response from the server and display the server's error message
      if (error.response && error.response.data && error.response.data.Message) {
        setModalMessage(error.response.data.Message);  // Display server's error message
      } else if (error.response && error.response.data) {
        setModalMessage(error.response.data);  // Display raw error data if no "Message" field is available
      } else {
        setModalMessage('An unexpected error occurred. Please try again.');  // Generic fallback message
      }

      onOpen();  // Open the modal to show the error message
    }
  };




  const handleLogout = () => {
    navigate('/');
  };

  return (
    <Flex
      height="100vh"
      width="100vw"
      justify="center"
      align="center"
      bg="gray.50"
      p={6}
    >
      <Box
        p={8}
        maxWidth="1200px"
        width="100%"
        borderWidth={1}
        borderRadius={8}
        boxShadow="lg"
        bg="white"
      >
        <Heading as="h2" size="xl" mb={6} textAlign="center">
          Student Dashboard
        </Heading>
        <Heading as="h6" size="l" mb={5} textAlign="center">
          User ID: {userId}, <i><b>{fullName}</b></i>
        </Heading>

        <Stack spacing={6}>
          <Text fontSize="xl"> Here's an overview of your current activities:</Text>

          <Box>
            <Heading as="h3" size="md" mb={4}>
              Your Active Classes
            </Heading>
            <Grid templateColumns="repeat(auto-fit, minmax(250px, 1fr))" gap={6} justifyContent="center" alignItems="center">
              {enrollments.length > 0 ? (
                enrollments.map((enrollment, index) => (
                  <Box
                    key={index}
                    p={4}
                    borderWidth={1}
                    borderRadius={8}
                    bg="teal.100"
                    textAlign="center"
                  >
                    <Heading size="sm">{enrollment.courseName}</Heading>
                    <Text mt={2}>Progress: {enrollment.progress}%</Text>
                  </Box>
                ))
              ) : (
                <Text>No active classes found.</Text>
              )}
            </Grid>
          </Box>

          <Box>
            <Heading as="h3" size="md" mb={4}>
              Available Classes
            </Heading>

            <Flex overflowY="auto" maxHeight="400px">
              <Table variant="simple" size="md" width="100%">
                <Thead>
                  <Tr>
                    <Th position="sticky" top={0} bg="gray.100" zIndex={1}>Course Name</Th>
                    <Th position="sticky" top={0} bg="gray.100" zIndex={1}>Description</Th>
                    <Th position="sticky" top={0} bg="gray.100" zIndex={1}>Max Seats</Th>
                    <Th position="sticky" top={0} bg="gray.100" zIndex={1}>Current Seats</Th>
                    <Th position="sticky" top={0} bg="gray.100" zIndex={1}>Start Date</Th>
                    <Th position="sticky" top={0} bg="gray.100" zIndex={1}>End Date</Th>
                    <Th position="sticky" top={0} bg="gray.100" zIndex={1}>Enroll</Th>
                  </Tr>
                </Thead>
                <Tbody>
                  {availableCourses.length > 0 ? (
                    availableCourses.map((course) => (
                      <Tr key={course.courseId}>
                        <Td>{course.courseName}</Td>
                        <Td>{course.description}</Td>
                        <Td>{course.maxSeats}</Td>
                        <Td>{course.currentSeats}</Td>
                        <Td>{new Date(course.startDate).toLocaleDateString()}</Td>
                        <Td>{new Date(course.endDate).toLocaleDateString()}</Td>
                        <Td>
                          <Button
                            colorScheme="teal"
                            size="sm"
                            onClick={() => handleEnroll(course.courseName)}
                          >
                            Add
                          </Button>
                        </Td>
                      </Tr>
                    ))
                  ) : (
                    <Tr>
                      <Td colSpan={7} textAlign="center">
                        No available classes.
                      </Td>
                    </Tr>
                  )}
                </Tbody>
              </Table>
            </Flex>
          </Box>

          <Flex justifyContent="flex-end">
            <Button colorScheme="red" size="md" onClick={handleLogout}>
              Logout
            </Button>
          </Flex>
        </Stack>

        {/* Modal for showing success or error messages */}
        <Modal isOpen={isOpen} onClose={onClose}>
          <ModalOverlay />
          <ModalContent>
            <ModalHeader>Enrollment Status</ModalHeader>
            <ModalCloseButton />
            <ModalBody>
              <Text>{modalMessage}</Text>
            </ModalBody>
            <ModalFooter>
              <Button colorScheme="blue" mr={3} onClick={onClose}>
                Close
              </Button>
            </ModalFooter>
          </ModalContent>
        </Modal>
      </Box>
    </Flex>
  );
};

export default Dashboard;
