import React from 'react';
import { Box, Heading, Text, Button, Stack, Flex, Grid } from '@chakra-ui/react';
import { useLocation, useNavigate } from 'react-router-dom';

// Define the prop type for userType
interface DashboardProps {
  userType: number;  // Explicitly declare userType as a number
}

const Dashboard: React.FC<DashboardProps> = ({ userType }) => {
  const navigate = useNavigate();  // Hook for logout navigation
  const location = useLocation();  // Hook to access state from Login page

  // Destructure the data passed via state
  const { statusCode, fullName, userId } = location.state || {};


  // Logout handler function
  const handleLogout = () => {
    // Clear any session storage or authentication data here if necessary
    // For example: sessionStorage.clear(); or localStorage.removeItem('token');

    // Navigate back to the login page
    navigate('/');
  };
  return (
    <Flex
      minHeight="100vh"
      align="center"
      justify="center"
      bg="gray.50"
      p={6}
    >
      <Box
        p={8}
        maxWidth="900px"
        borderWidth={1}
        borderRadius={8}
        boxShadow="lg"
        bg="white"
      >
        {/* Conditionally render the dashboard title based on userType */}
        <Heading as="h2" size="xl" mb={6} textAlign="center">
          {userType === 1 ? 'Admin Dashboard' : 'Student Dashboard'}
        </Heading>
        <Heading as="h2" size="xl" mb={6} textAlign="center">
         User {userId}
        </Heading>

        <Stack spacing={6}>
          <Text fontSize="xl">Welcome back <b>{fullName}</b>. Here's an overview of your current activities:</Text>

          {/* Section for Current Enrollments */}
          <Box>
            <Heading as="h3" size="md" mb={4}>
              Your Active Classes
            </Heading>
            {/* You can render a list of active classes dynamically */}
            <Grid templateColumns="repeat(3, 1fr)" gap={6}>
              <Box p={4} borderWidth={1} borderRadius={8} bg="teal.100">
                <Heading size="sm">Class 1</Heading>
                <Text mt={2}>Progress: 50%</Text>
              </Box>
              <Box p={4} borderWidth={1} borderRadius={8} bg="teal.100">
                <Heading size="sm">Class 2</Heading>
                <Text mt={2}>Progress: 80%</Text>
              </Box>
              <Box p={4} borderWidth={1} borderRadius={8} bg="teal.100">
                <Heading size="sm">Class 3</Heading>
                <Text mt={2}>Progress: 20%</Text>
              </Box>
            </Grid>
          </Box>

          {/* Section for Available Classes */}
          <Box>
            <Heading as="h3" size="md" mb={4}>
              Available Classes
            </Heading>
            <Button colorScheme="teal" size="md">
              View Available Classes
            </Button>
          </Box>

          {/* Logout Button */}
          <Flex justifyContent="flex-end">
            <Button colorScheme="red" size="md" onClick={handleLogout}>
              Logout
            </Button>
          </Flex>
        </Stack>
      </Box>
    </Flex>
  );
};

export default Dashboard;
