import React, { useEffect, useState } from 'react';
import { Box, Table, Thead, Tbody, Tr, Th, Td, TableContainer, Heading, Spinner, Text } from '@chakra-ui/react';
import axios from 'axios';

interface Course {
    id: number;  // Example fields, adjust based on actual data
    name: string;
    description: string;
  }

  const CoursePage = () => {
    const [courses, setCourses] = useState<Course[]>([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
      const fetchCourses = async () => {
        try {
          const response = await axios.get('http://localhost:5000/api/Course/ShowAllCourses');
          setCourses(response.data);
          setLoading(false);
        } catch (err) {
          setError('Failed to fetch courses.');
          setLoading(false);
        }
      };

      fetchCourses();
    }, []);

    if (loading) {
        return (
          <Box textAlign="center" mt={10}>
            <Spinner size="xl" />
          </Box>
        );
      }

      if (error) {
        return (
          <Box textAlign="center" mt={10}>
            <Text color="red.500">{error}</Text>
          </Box>
        );
      }

      return (
        <Box p={8}>
          <Heading as="h2" size="xl" mb={6} textAlign="center">Available Courses</Heading>
          <TableContainer>
            <Table variant="simple">
              <Thead>
                <Tr>
                  <Th>ID</Th>
                  <Th>Name</Th>
                  <Th>Description</Th>
                </Tr>
              </Thead>
              <Tbody>
                {courses.map((course) => (
                  <Tr key={course.id}>
                    <Td>{course.id}</Td>
                    <Td>{course.name}</Td>
                    <Td>{course.description}</Td>
                  </Tr>
                ))}
              </Tbody>
            </Table>
          </TableContainer>
        </Box>
      );
    };

    export default CoursePage;
