import React, { useState } from 'react';
import { Box, Button, Center, FormControl, FormLabel, Input, VStack } from '@chakra-ui/react';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = () => {
    // Handle the login logic (API call or form validation)
    console.log('Email:', email);
    console.log('Password:', password);
  };

  return (
    <Center h="100vh">
      <Box w="md" p={6} boxShadow="lg" borderRadius="md">
        <VStack spacing={4}>
          <FormControl id="email">
            <FormLabel>Email</FormLabel>
            <Input 
              type="email" 
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Enter your institutional email"
            />
          </FormControl>

          <FormControl id="password">
            <FormLabel>Password</FormLabel>
            <Input 
              type="password" 
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter your password"
            />
          </FormControl>

          <Button colorScheme="teal" onClick={handleLogin} width="full">
            Login
          </Button>
        </VStack>
      </Box>
    </Center>
  );
};

export default Login;
