import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Box, Button, FormControl, FormLabel, Input, Stack, Heading, Flex, Text } from '@chakra-ui/react';
import axios from 'axios';

const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();

  try {
      const response = await axios.post('YOUR_BACKEND_LOGIN_ENDPOINT', {
        email,
        password,
      });
      if (response.status === 200) {
        // Assuming the backend sends a token or some data upon successful login
        const token = response.data.token;
        // You might want to store the token in localStorage/sessionStorage
        localStorage.setItem('token', token);

        // Navigate to the dashboard on successful login
        navigate('/dashboard');
      } else {
        setErrorMessage('Login failed. Please check your credentials.');
      }
    } catch (error) {
      setErrorMessage('An error occurred during login. Please try again.');
    }
  };

  return (
    <Flex
      minHeight="100vh"
      align="center"
      justify="center"
      bg="gray.50"
    >
      <Box
        p={8}
        maxWidth="400px"
        borderWidth={1}
        borderRadius={8}
        boxShadow="lg"
        bg="white"
      >
        <Box textAlign="center" mb={6}>
          <Heading as="h2" size="lg">Student Management</Heading>
        </Box>
        <form>
          <Stack spacing={4}>
            <FormControl id="email" isRequired>
              <FormLabel>Email address</FormLabel>
              <Input type="email" placeholder="Enter your email" />
            </FormControl>
            <FormControl id="password" isRequired>
              <FormLabel>Password</FormLabel>
              <Input type="password" placeholder="Enter your password" />
            </FormControl>
            <Button colorScheme="blue" size="lg" type="submit" width="full">
              Sign In
            </Button>
          </Stack>
        </form>
      </Box>
    </Flex>
  );
};

export default Login;
